package com.example.lessondz3v4;

import androidx.fragment.app.Fragment;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;


import com.example.lessondz3v4.databinding.FragmentCountryBinding;

import java.util.ArrayList;


public class CountryFragment extends Fragment {


    private FragmentCountryBinding binding;
    private ArrayList<Country> countryList;
    private CountryAdapter adapter;
    private Integer position;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentCountryBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        position = getArguments().getInt("key");
        checkPosition(position);
        adapter = new CountryAdapter(countryList);
        binding.recyclerViewCountry.setAdapter(adapter);
    }

    private void checkPosition(Integer position) {
        if (position == 0) {
            loadNorthAmerica();
        } else if (position == 1) {
            loadSouthAmerica();
        } else if (position == 2) {
            loadEurasia();
        } else if (position == 3) {
            loadAfrica();
        } else if (position == 4) {
            loadAustralia();
        }
    }

    private void loadAfrica() {
        countryList = new ArrayList<>();
        countryList.add(new Country("Tunisia", "Tunis", "data:image/png;base64,"));
        countryList.add(new Country("Nigeria", "Abuja", "data:image/png;base64,"));
        countryList.add(new Country("Morocco", "Rabat", "data:image/png;base64,"));
        countryList.add(new Country("Senegal", "Dakar", "data:image/png;base64,"));
        countryList.add(new Country("Algeria", "Algiers", "data:image/png;base64,"));
    }


    private void loadAustralia() {
        countryList = new ArrayList<>();
        countryList.add(new Country("Australia", "Canberra", "data:image/jpeg;base64,"));
        countryList.add(new Country("Papua New Guinea", "Port-Moresby", "data:image/jpeg;base64,"));
        countryList.add(new Country("New Zealand", "Wellington", "https://upload.wikimedia.org/wikipedia/commons/6/6b/Flag_of_Wellington_City%2C_New_Zealand.gif"));
        countryList.add(new Country("Fiji", "Suva", "data:image/png;base64,"));
        countryList.add(new Country("Solomon Islands", "Honiara", "data:image/jpeg;base64,"));
    }

    private void loadEurasia() {
        countryList = new ArrayList<>();
        countryList.add(new Country("France", "Paris", "https://upload.wikimedia.org/wikipedia/commons/6/62/Flag_of_France.png"));
        countryList.add(new Country("Spain", "Madrid", "https://upload.wikimedia.org/wikipedia/commons/thumb/9/9a/Flag_of_Spain.svg/2560px-Flag_of_Spain.svg.png"));
        countryList.add(new Country("Azerbaijan", "Baku", "https://upload.wikimedia.org/wikipedia/commons/thumb/c/cf/Flag_of_Azerbaijan_Democtratic_Republic.PNG/800px-Flag_of_Azerbaijan_Democtratic_Republic.PNG"));
        countryList.add(new Country("Ukraine", "Kiev", "https://upload.wikimedia.org/wikipedia/commons/thumb/d/d2/Flag_of_Ukraine.png/640px-Flag_of_Ukraine.png"));
        countryList.add(new Country("Kyrgyzstan", "Bishkek", "https://upload.wikimedia.org/wikipedia/commons/thumb/b/ba/Flag_of_Kyrgyzstan.png/1200px-Flag_of_Kyrgyzstan.png"));
    }

    private void loadSouthAmerica() {
        countryList = new ArrayList<>();
        countryList.add(new Country("Brazil", "Brasilia", "data:image/png;base64,"));
        countryList.add(new Country("Argentina", "Buenos-Aires", "data:image/png;base64,"));
        countryList.add(new Country("Peru", "Lima", "https://upload.wikimedia.org/wikipedia/commons/thumb/d/df/Flag_of_Peru_%28state%29.svg/800px-Flag_of_Peru_%28state%29.svg.png"));
        countryList.add(new Country("Colombia", "Bogota", "data:image/png;base64,"));
        countryList.add(new Country("Chile", "Santiago", "https://cdn.britannica.com/85/7485-004-00B07230/Flag-Chile.jpg"));
    }

    private void loadNorthAmerica() {
        countryList = new ArrayList<>();
        countryList.add(new Country("United States of America", "Washington", "https://upload.wikimedia.org/wikipedia/en/thumb/a/a4/Flag_of_the_United_States.svg/1200px-Flag_of_the_United_States.svg.png"));
        countryList.add(new Country("Mexico", "Mexico", "https://cdn.britannica.com/73/2573-004-29818847/Flag-Mexico.jpg"));
        countryList.add(new Country("Canada", "Ottawa", "https://upload.wikimedia.org/wikipedia/commons/d/d9/Flag_of_Canada_%28Pantone%29.svg"));
        countryList.add(new Country("Cuba", "Havana", "https://cdn.britannica.com/10/5110-004-38075E57/Flag-Cuba.jpg"));
        countryList.add(new Country("Greenland", "Nuuk", "https://upload.wikimedia.org/wikipedia/commons/thumb/0/09/Flag_of_Greenland.svg/2560px-Flag_of_Greenland.svg.png"));
    }
}
